#include <bits/stdc++.h>
using namespace std;
typedef long long LL;

class Point {
public:
  LL x, y;
  Point(LL _x = 0, LL _y = 0) : x(_x), y(_y) {}
  Point operator+(const Point &p) const { return Point(x + p.x, y + p.y); }
  Point operator-(const Point &p) const { return Point(x - p.x, y - p.y); }
  bool operator<(const Point &p) const {
    if (x != p.x)
      return x < p.x;
    return y < p.y;
  }
  bool operator==(const Point &p) const { return x == p.x && y == p.y; }
  friend istream &operator>>(istream &cin, Point &p) {
    cin >> p.x >> p.y;
    return cin;
  }
};

LL cross(const Point &p, const Point &q) { return p.x * q.y - p.y * q.x; }

LL seen_largest_area;
LL Triangle(Point A, Point B, Point C) {
  LL v = cross(A, B) + cross(B, C) + cross(C, A);
  v = v > 0 ? v : -v;
  seen_largest_area = max(seen_largest_area, v);
  return v;
}

void ComputeConvexHull(vector<Point> &hull, const vector<Point> &points) {
  hull.clear();
  for (auto &p : points) {
    while (hull.size() >= 2 &&
           cross(hull.back() - hull[hull.size() - 2], p - hull.back()) <= 0)
      hull.pop_back();
    hull.push_back(p);
  }
}

int main() {
  int N;
  cin >> N;
  vector<Point> p(N);
  for (int i = 0; i < N; i++)
    cin >> p[i];
  sort(p.begin(), p.end());
  p.resize(unique(p.begin(), p.end()) - p.begin());
  vector<Point> upper_hull, lower_hull;
  ComputeConvexHull(lower_hull, p);
  reverse(p.begin(), p.end());
  ComputeConvexHull(upper_hull, p);
  for (size_t i = 1; i + 1 < upper_hull.size(); i++)
    lower_hull.push_back(upper_hull[i]);

  size_t M = lower_hull.size();
  for (size_t i = 0; i < M; i++)
    lower_hull.push_back(lower_hull[i]);

  seen_largest_area = 0;
  for (size_t i = 0; i < M; i++)
    for (size_t j = i + 1, k = j; j < i + M; j++) {
      while (k < i + M &&
             Triangle(lower_hull[i], lower_hull[j], lower_hull[k + 1]) >
                 Triangle(lower_hull[i], lower_hull[j], lower_hull[k]))
        ++k;
    }
  printf("%.9f\n", (double)seen_largest_area / 2.0);
  return 0;
}

#include <bits/stdc++.h>
using namespace std;

int N, K;
vector<int> A;

bool Test(int mask) {
  int seeking = 0;
  for (int start = 0; start < N; start++) {
    if ((mask&(seeking|A[start])) != (mask&seeking)) {
      int now = 0, groups = 0;
      for (int i = start+1; i <= start+N; i++) {
        now |= A[i];
        if ((now&mask) == mask) {
          groups++;
          now = 0;
        }
      }
      groups += ((now&mask) == mask);
      if (groups >= K) return true;
    }
    seeking |= A[start];
  }
  return false;
}

int main() {
  cin >> N >> K;
  A.resize(N*2);
  for (int i = 0; i < N; i++) cin >> A[i];
  for (int i = 0; i < N; i++) A[N+i] = A[i];
  int mask = 0;
  for (int bit = 29; bit >= 0; bit--) {
    if (Test(mask + (1<<bit))) {
      mask += (1<<bit);
    }
  }
  cout << mask << endl;
  return 0;
}

#include <bits/stdc++.h>
using namespace std;
typedef long long LL;

const LL MOD = 1e9 + 7;

LL bigmod(LL a, LL n) {
  LL b = 1;
  while (n) {
    if (n % 2)
      b = b * a % MOD;
    a = a * a % MOD;
    n /= 2;
  }
  return b;
}

LL npower[5005];
LL X[5005], Y[5005];

int main() {
  int N, R, C, K;
  cin >> N >> R >> C >> K;
  for (int i = 1; i <= 5000; i++)
    npower[i] = bigmod(i, N);
  for (int r = 0; r <= R; r++) {
    for (int i = 0; i <= R - r; i++) {
      X[r] += (npower[i + 1] - npower[i]) *
              (npower[R - r - i + 1] - npower[R - r - i]) % MOD;
    }
    X[r] = (X[r] % MOD + MOD) % MOD;
  }
  for (int c = 0; c <= C; c++) {
    for (int i = 0; i <= C - c; i++) {
      Y[c] += (npower[i + 1] - npower[i]) *
              (npower[C - c - i + 1] - npower[C - c - i]) % MOD;
    }
    Y[c] = (Y[c] % MOD + MOD) % MOD;
  }

  LL ans = 0;
  for (int r = 1; r <= R; r++)
    for (int c = 1; c <= C; c++) {
      if (r * c >= K) {
        ans += X[r] * Y[c] % MOD;
      }
    }
  cout << ans % MOD << endl;
  return 0;
}

