#include <bits/stdc++.h>
using namespace std;

typedef long long LL;
string S;
string T[26];
LL K;

LL len[26][64];
const LL LIMIT = (LL)1e15;

void add(LL &x, LL v) {
  // -1 ä»£è¡¨è¶…éé•·åº¦ã€‚
  if (x == -1 || v == -1) {
    x = -1;
  } else {
    x += v;
    if (x >= LIMIT)
      x = -1;
  }
}

void pre() {
  for (int i = 0; i < 26; i++) {
    len[i][0] = 1;
    len[i][1] = T[i].size();
  }
  for (int t = 2; t < 64; t++)
    for (int i = 0; i < 26; i++)
      for (auto x : T[i])
        add(len[i][t], len[x - 'a'][t - 1]);
}

char ans;

// å¦‚æœé‚„ä¸å¤ çš„è©±å°±å›å‚³ falseï¼Œå¦‚æœæ‰¾åˆ°äº†å°±å›å‚³ true.
bool ask(LL &m, char root, LL k) {
  if (k == 0) {
    --m;
    ans = root;
    return (m == 0);
  }
  if (k < 64 && len[root - 'a'][k] != -1 && len[root - 'a'][k] < m) {
    m -= len[root - 'a'][k];
    return false;
  }
  if (k <= 100) {
    for (auto child : T[root - 'a']) {
      if (ask(m, child, k - 1)) {
        return true;
      }
    }
  } else {
    // æ‰¾å‡º cycle, ç„¶å¾Œé£›åˆ°ç¬¬ä¸€å€‹å°æ–¼100å±¤çš„åœ°æ–¹ã€‚
    int pos[26] = {};
    int now = root - 'a';
    int cnt = 1;
    while (pos[now] == 0) {
      pos[now] = cnt;
      now = T[now][0] - 'a';
      cnt++;
    }
    int cycle = cnt - pos[now];
    k -= (cnt - 1);
    k -= (k - 100) / cycle * cycle;
    k -= cycle;
    return ask(m, now + 'a', k);
  }
  return false;
}

void solve() {
  LL m;
  cin >> m;
  for (size_t i = 0; i < S.size(); i++) {
    if (ask(m, S[i], K))
      break;
  }
  cout << ans << '\n';
}

int main() {
  cin >> S;
  for (int i = 0; i < 26; i++)
    cin >> T[i];
  cin >> K;
  // é è™•ç†ï¼Œè¨ˆç®—ä¸è¶…é 64 å±¤ï¼Œä»¥æŸå­—å…ƒç‚ºæ¨¹æ ¹çš„å­æ¨¹å¤§å°ã€‚
  pre();

  int M;
  cin >> M;
  while (M--)
    solve();
  return 0;
}

