  UP = 2,
  DOWN = 3
};

inline bool IsNeighbor(int x, int y) {
  return abs(r[x]-r[y]) + abs(c[x]-c[y]) == 1;
}

void dfs(int x) {
  visit[x] = 1;
  for (int dir = 0; dir < 4; dir++) {
    int y = link[x][dir];
    if (IsNeighbor(x, y) && !visit[y]) {
      face[y] = (dir^1);
      s[y] = DSTR[dir] + " " + to_string(dir/2? c[y]: r[y]);
      dfs(y);
    }
  }
}

// ç´€éŒ„å„ªå…ˆé †åº (å¾—å…ˆæœ‰ y æ‰èƒ½æœ‰ x)ã€‚
vector<int> deps[MAXB];
inline void AddLink(int x, int y) {
  if (x == -1 || y == -1) return;
  deps[x].push_back(y);
}

// ç´€éŒ„è§£ç­”ç”¨çš„ã€‚
vector<int> solution;
bool no_solution = false;
void topological_sort(int x) {
  visit[x] = 1;
  for (auto y : deps[x]) {
    if (visit[y] == 0)
      topological_sort(y);
    else if (visit[y] == 1)
      no_solution = 1;
  }
  solution.push_back(x);
  visit[x] = 2;
}

int main() {
  int N, M, B;
  cin >> N >> M >> B;
  for (int i = 0; i < B; i++) {
    cin >> r[i] >> c[i];
    rows[r[i]].push_back({c[i], i});
    cols[c[i]].push_back({r[i], i});
  }
  for (int i = 1; i < MAXB; i++) {
    sort(rows[i].begin(), rows[i].end());
    sort(cols[i].begin(), cols[i].end());
  }

  memset(link, -1, sizeof(link));
  for (int i = 1; i < MAXB; i++) {
    for (size_t j = 1; j < rows[i].size(); j++) {
      int prev = rows[i][j-1].second;
      int now = rows[i][j].second;
      link[prev][RIGHT] = now;
      link[now][LEFT] = prev;
    }
    for (size_t j = 1; j < cols[i].size(); j++) {
      int prev = cols[i][j-1].second;
      int now = cols[i][j].second;
      link[prev][DOWN] = now;
      link[now][UP] = prev;
    }
  }
  dfs(0);
  for (int x = 1; x < B; x++) {
    AddLink(x, link[x][face[x]]);
    AddLink(link[x][face[x]^1], x);
  }
  memset(visit, 0, sizeof(visit));
  for (int x = 0; x < B; x++) {
    if (!visit[x])
      topological_sort(x);
  }
  if (no_solution) {
    cout << "impossible" << endl;
  } else {
    cout << "possible" << '\n';
    for (auto x : solution)
      if (x != 0)
        cout << s[x] << '\n';
  }
  return 0;
}

#include <bits/stdc++.h>
using namespace std;

struct Edge {
  int to, cap, rev;
  Edge(int _to, int _cap, int _rev): to(_to), cap(_cap), rev(_rev) {}
};

class FlowNetwork {
  public:
    int n, sink;
    vector<vector<Edge>> adj;
    int mark;
    vector<int> marks;
    FlowNetwork(int _n, int _sink): n(_n), sink(_sink) {
      adj.resize(n);
      marks.resize(n, 0);
      mark = 1;
    }
    void AddEdge(int x, int y, int c = 1) {
      adj[x].emplace_back(y, c, adj[y].size());
      adj[y].emplace_back(x, 0, adj[x].size()-1);
    }
    bool _DFS(int x) {
      marks[x] = mark;
      if (x == sink) return true;
      for (auto& e: adj[x]) {
        if (marks[e.to] != mark && e.cap > 0 && _DFS(e.to)) {
          adj[e.to][e.rev].cap++;
          e.cap--;
          return true;
        }
      }
      return false;
    }
    bool DFS(int s) {
      if (_DFS(s)) {
        mark++;
        return true;
      }
      return false;
    }
};

int main() {
  int N, K, M;
  cin >> N >> K >> M;
  FlowNetwork g(N*K+1, N*K);
  auto enc = [&](int i, int j) {
    return (i-1)*K+j;
  };
  for (int j = 0; j < K; j++)
    g.AddEdge(enc(N, j), g.sink, K);
  while (M--) {
    int x, y;
    cin >> x >> y;
    for (int j = 0; j < K; j++)
      g.AddEdge(enc(x, j), enc(y, (j+1)%K));
  }
  int ans = 0;
  for (int i = 1; i <= K; i++) {
    if (g.DFS(enc(i, i%K)))
      ++ans;
  }
  cout << ans << endl;
  return 0;
}

