const int LEAF_OFFSET = (1<<19);
int tree[(1<<20)];
inline int GetMinLabel(int l, int r) {
  if (l == 0 || r == 0) return l+r;
  return min(l, r);
}
void Insert(int x, int label) {
  x += LEAF_OFFSET;
  tree[x] = label;
  for (x/=2; x; x/=2)
    tree[x] = GetMinLabel(tree[x*2], tree[x*2+1]);
}
void Remove(int x) {
  x += LEAF_OFFSET;
  tree[x] = 0;
  for (x/=2; x; x/=2)
    tree[x] = GetMinLabel(tree[x*2], tree[x*2+1]);
}
int Query(int l) {
  l += LEAF_OFFSET;
  int ans = tree[l];
  while (l) {
    if (l%2==0)
      ans = GetMinLabel(ans, tree[l+1]);
    l/=2;
  }
  return ans;
}


