#include <bits/stdc++.h>
using namespace std;

bool proper = true;
bool correct = true;

string S;

string RemoveSpace(string& S) {
  string T = "";
  for (auto x: S) if (x != ' ') T += x;
  return T;
}

// åˆæ³•çš„åºåˆ—æœƒæ˜¯ x+x+x+...+x
void UpdateCorrectness(vector<char>& c) {
  if (c.size()%2==0) { correct = false; return; }
  for (size_t i = 1; i < c.size(); i++)
    if (c[i] == c[i-1]) {
      correct = false;
      return;
    }
  if (c[0] != 'x') { correct = false; return; }
}

int main() {
  getline(cin, S);
  S = RemoveSpace(S);
  vector<vector<char>> levels(1);
  for (size_t i = 0, now = 1; i < S.size(); i++) {
    if (S[i] == '(') {
      levels.back().push_back('x');
      ++now;
      levels.resize(now);
    } else if (S[i] == ')') {
      UpdateCorrectness(levels.back());
      if (levels.back().size() != 3) proper = false;
      levels.pop_back();
      --now;
      if (now <= 0) {
        correct = false;
        break;
      }
      levels.resize(now);
    } else if (S[i] >= 'a' && S[i] <= 'z') {
      levels.back().push_back('x');
    } else {
      levels.back().push_back('+');
    }
  }

  if (levels.size() != 1) {
    correct = false;
  } else {
    UpdateCorrectness(levels[0]);
    if (S[0] == '(' && levels[0].size() == 1) proper = false;
    if (levels[0].size() != 1 && levels[0].size() != 3) proper = false;
  }

  if (correct && proper) {
    cout << "proper" << endl;
  } else if (correct && !proper) {
    cout << "improper" << endl;
  } else if (!correct) {
    cout << "error" << endl;
  }
  return 0;
}

#include <bits/stdc++.h>
using namespace std;

class Graph {
public:
  int n;
  vector<vector<int>> adj;
  vector<vector<int>> rev;
  vector<int> scc;
  vector<int> visited;
  vector<vector<int>> groups;
  int nscc;
  Graph(int _n) : n(_n) {
    adj.resize(n);
    rev.resize(n);
    visited.resize(n, 0);
    scc.resize(n, 0);
    groups.resize(n+1);
    nscc = 0;
  }
  void AddEdge(int x, int y) {
    adj[x].push_back(y);
    rev[y].push_back(x);
  }
  void ComputeSCC() {
    vector<int> stack;
    for (int i = 0; i < n; i++)
      if (visited[i] == 0)
        _dfs1(stack, i);
    while (!stack.empty()) {
      int x = stack.back();
      stack.pop_back();
      if (visited[x] == 1) {
        ++nscc;
        _dfs2(x);
      }
    }
  }

private:
  void _dfs1(vector<int> &stack, int x) {
    visited[x] = 1;
    for (int y : adj[x])
      if (visited[y] == 0)
        _dfs1(stack, y);
    stack.push_back(x);
  }
  void _dfs2(int x) {
    visited[x] = 2;
    scc[x] = nscc;
    groups[nscc].push_back(x);
    for (int y : rev[x])
      if (visited[y] == 1)
        _dfs2(y);
  }
};

int ReadNode() {
  int l;
  string c;
  cin >> l >> c;
  return l * 2 + (c[0] == 'R');
}

int main() {
  int k, n;
  cin >> k >> n;
  Graph g(k * 2 + 10);
  for (int i = 0; i < n; i++) {
    int x[4];
    for (int j = 0; j < 3; j++)
      x[j] = ReadNode();
    x[3] = x[0];
    for (int j = 0; j < 3; j++) {
      g.AddEdge(x[j] ^ 1, x[j + 1]);
      g.AddEdge(x[j + 1] ^ 1, x[j]);
    }
  }
  g.ComputeSCC();

  // åˆ¤æ–·æ˜¯å¦ç„¡è§£ã€‚
  for (int i = 1; i <= k; i++) {
    if (g.scc[i * 2] == g.scc[i * 2 + 1]) {
      cout << "-1" << endl;
      return 0;
    }
  }

  // å¾ç¸®åœ–ä¹‹å¾Œå¾ç›¸ä¾é †åºæœ«ç«¯é–‹å§‹ï¼Œä»¥è²ªæ±‚æ³•æ‰¾å‡ºä¸€çµ„ 2-SAT è§£ã€‚
  vector<bool> taken(g.n, false);
  for (int scc = g.nscc; scc > 0; scc--) {
    bool ok = true;
    for (int x : g.groups[scc])
      if (taken[x ^ 1])
        ok = false;
    if (ok) {
      for (int x : g.groups[scc])
        taken[x] = true;
    }
  }

  // æŠŠè§£ç­”è¼¸å‡ºã€‚
  string ret = "";
  for (int i = 1; i <= k; i++) {
    if (taken[i * 2] == true) {
      ret += "B";
    } else {
      ret += "R";
    }
  }

  cout << ret << endl;
  return 0;
}

#include <bits/stdc++.h>
using namespace std;
typedef long long LL;

LL inv(LL x, LL y, LL p = 1, LL q = 0, LL r = 0, LL s = 1) {
  return y ? p : inv(y, x % y, r, s, p - (x / y) * r, q - (x / y) * s);
}

// ä½¿ç”¨æ¢ä»¶ï¼š
// (1) 0 <= x1 < m1; 0 <= x2 < m2
// (2) m2 * m2 ä¸æœƒæº¢ä½ã€‚
// (3) 2 * m1 * m2 ä¸æœƒæº¢ä½ã€‚
LL CRT2(LL x1, LL m1, LL x2, LL m2) {
  LL t = inv(m1, m2);
  t %= m2;
  LL z = (t * (x2 - x1) % m2 + m2) % m2;
  return (x1 + z * m1) % (m1 * m2);
}

void solve() {
  int N[3], R[3] = {};
  for (int i = 0; i < 3; i++)
    cin >> N[i];
  for (int i = 0; i < 3; i++)
    cin >> R[i];
  vector<int> candidates[3];
  for (int i = 0; i < 3; i++)
    for (LL x = 0; x < N[i]; x++) {
      if (x * x * x % N[i] == R[i]) {
        candidates[i].push_back(x);
      }
    }
  vector<LL> sol;

  for (auto r1 : candidates[0])
    for (auto r2 : candidates[1])
      for (auto r3 : candidates[2]) {
        LL x = CRT2(r1, N[0], r2, N[1]);
        x = CRT2(x, (LL)N[0] * N[1], r3, N[2]);
        sol.push_back(x);
      }

  sort(sol.begin(), sol.end());
  int maxn = max(N[0], max(N[1], N[2]));
  for (auto s : sol)
    if (s * s > maxn || s * s * s > maxn) {
      cout << s << endl;
      break;
    }
}

int main() {
  int T;
  cin >> T;
  while (T--)
    solve();
  return 0;
}

