      jobs.push_back(i);
      for (int j = 0; j < w; j++) {
        if (--d[i + j] < 0)
          bad = 1;
      }
    }
  }

  priority_queue<pair<int, int>> s;
  for (int i = 0; i < m; i++)
    s.push({ws[i] - schedule.size(), i});
  vector<vector<pair<int, int>>> delay(n + 1);
  int now = 0;

  for (int t : jobs) {
    while (now <= t) {
      for (auto x : delay[now])
        s.push(x);
      now++;
    }
    if (s.empty()) {
      bad = 1;
      break;
    }
    auto [remain, i] = s.top();
    s.pop();
    schedule[i].push_back(t);
    if (t + w + h <= n)
      delay[t + w + h].push_back({remain - 1, i});
  }
  for (int i = 0; i < m; i++) {
    if (schedule[i].size() != ws[i])
      bad = 1;
  }
  if (bad) {
    cout << "-1" << endl;
  } else {
    cout << "1";
    for (int i = 0; i < m; i++) {
      for (int j = 0; j < schedule[i].size(); j++) {
        cout << (j == 0 ? '\n' : ' ');
        cout << schedule[i][j];
      }
    }
    cout << '\n';
  }
  return 0;
}

#include <bits/stdc++.h>
using namespace std;
typedef long long LL;

LL inv(LL x, LL y, LL p = 1, LL q = 0, LL r = 0, LL s = 1) {
  return y ? p : inv(y, x % y, r, s, p - (x / y) * r, q - (x / y) * s);
}

// ä½¿ç”¨æ¢ä»¶ï¼š
// (1) 0 <= x1 < m1; 0 <= x2 < m2
// (2) m2 * m2 ä¸æœƒæº¢ä½ã€‚
// (3) 2 * m1 * m2 ä¸æœƒæº¢ä½ã€‚
LL CRT2(LL x1, LL m1, LL x2, LL m2) {
  LL t = inv(m1, m2);
  t %= m2;
  LL z = (t * (x2 - x1) % m2 + m2) % m2;
  return (x1 + z * m1) % (m1 * m2);
}

void solve() {
  int N[3], R[3] = {};
  for (int i = 0; i < 3; i++)
    cin >> N[i];
  for (int i = 0; i < 3; i++)
    cin >> R[i];
  vector<int> candidates[3];
  for (int i = 0; i < 3; i++)
    for (LL x = 0; x < N[i]; x++) {
      if (x * x * x % N[i] == R[i]) {
        candidates[i].push_back(x);
      }
    }
  vector<LL> sol;

  for (auto r1 : candidates[0])
    for (auto r2 : candidates[1])
      for (auto r3 : candidates[2]) {
        LL x = CRT2(r1, N[0], r2, N[1]);
        x = CRT2(x, (LL)N[0] * N[1], r3, N[2]);
        sol.push_back(x);
      }

  sort(sol.begin(), sol.end());
  int maxn = max(N[0], max(N[1], N[2]));
  for (auto s : sol)
    if (s * s > maxn || s * s * s > maxn) {
      cout << s << endl;
      break;
    }
}

int main() {
  int T;
  cin >> T;
  while (T--)
    solve();
  return 0;
}

#include <bits/stdc++.h>
using namespace std;

bool test(const vector<int> &a, int twice_x) {
  int n = a.size();
  vector<int> lmax(n), rmax(n), lmin(n), rmin(n);

  // æ‰¾å‡ºç¬¬ä¸€æ®µã€‚
  int start = 0;
  while (start < n && a[start] * 2 <= twice_x)
    start++;
  if (start >= n - 1)
    return true;

  // è™•ç†ç¬¬äºŒæ®µèˆ‡ç¬¬ä¸‰æ®µã€‚
  lmax[start] = a[start];
  lmin[start] = a[start];
  rmax[n - 1] = a[n - 1];
  rmin[n - 1] = a[n - 1];

  for (int i = start + 1; i < n; i++) {
    lmax[i] = max(lmax[i - 1], a[i]);
    lmin[i] = min(lmin[i - 1], a[i]);
  }
  for (int i = n - 2; i >= start; i--) {
    rmax[i] = max(rmax[i + 1], a[i]);
    rmin[i] = min(rmin[i + 1], a[i]);
  }

  // æšèˆ‰å¯èƒ½çš„åˆ†ç•Œé»ã€‚
  for (int i = start; i + 1 < n; i++) {
    int L1_minus_x = lmax[i] - twice_x;
    int L2_minus_x = rmax[start] - twice_x;
    if (L1_minus_x <= lmin[i] && L2_minus_x <= rmin[i + 1])
      return true;
  }

  return false;
}

