int main() {
  int N, M;
  cin.sync_with_stdio(false);
  cin >> N >> M;
  vector<long long> A(N), B(N);

  for (int i = 0; i < N; i++) cin >> A[i];
  for (int i = 0; i < N; i++) cin >> B[i];

  // å…ˆç®—å‡º Ayu æ‹¿åˆ°æ¯é¡†æ°£çƒçš„æ™‚é–“é»ï¼Œä¸¦ç®—å‡º Ayu å¯ä»¥è§£å¹¾é¡Œã€‚
  int nsolved = 0;
  for (int i = 1; i < N; i++) A[i] += A[i - 1];
  for (int i = 0; i < N; i++) nsolved += (A[i] <= M);

  // æˆ‘å€‘ç›®æ¨™å°±æ˜¯è¦è®“ Budi è§£å‡ºç¬¬ nsolved çš„æ™‚é–“åš´æ ¼å¤§æ–¼ Mã€‚
  long long t = 0;
  vector<pair<int, int>> stack;
  for (int i = 0, j = 0; i < nsolved; i++) {
    int balloons = 0;
    while (!stack.empty() && B[stack.back().first] <= B[i]) {
      balloons += stack.back().second;
      t -= stack.back().second * B[stack.back().first];
      stack.pop_back();
    }
    t += B[i] * (balloons + 1);
    while (j < nsolved && A[j] <= t) {
      ++j;
      ++balloons;
      t += B[i];
    }
    stack.push_back(make_pair(i, balloons));
  }

  // å¦‚æœé‚„æ˜¯åœ¨ M åˆ†é˜å…§è§£å‡ºä¾†äº†ï¼Œå°±è¼¸å‡º -1ã€‚
  if (t <= M) {
    cout << "-1" << endl;
    return 0;
  }

  // è¨ˆç®—æ¯é¡†æ°£çƒè¢«æˆ³ç ´çš„æ™‚é–“ã€‚
  t = 0;
  int j = 0;
  vector<long long> ans;
  for (auto [i, b] : stack) {
    while (j < i) t += B[j++];
    for (int l = 0; l < b; l++)
      ans.push_back(t += B[i]);
  }

  // è¼¸å‡ºç­”æ¡ˆã€‚
  while (!ans.empty() && ans.back() > M) ans.pop_back();
  cout << ans.size() << endl;
  for (auto x : ans) cout << x << " ";
  cout << endl;
  return 0;
}

