// ä¸€å€‹æ´»ç”Ÿç”Ÿã€è·³è„«æ¡†æ¶ï¼ˆå¥½åƒä¸å¤ªæ‡‰è©²ï¼‰çš„ Fenwick Treeã€‚
int bit[100005];

// æŠŠ x é€™æ ¼çš„å€¼åŠ ä¸Š vã€‚
void add(int x, int v) {
  while (x <= N) {
    bit[x] += v;
    x += (x & -x);
  }
}

// è¨ˆç®—åºåˆ—å‰ x é …çš„ç¸½å’Œã€‚
int ask(int x) {
  int ret = 0;
  while (x) {
    ret += bit[x];
    x -= (x & -x);
  }
  return ret;
}

// è¨ˆç®—åºåˆ—å¾ç¬¬ l é …åˆ°ç¬¬ r é …çš„ç¸½å’Œã€‚
int ask(int l, int r) { return ask(r) - ask(l - 1); }

int main() {
  cin >> N >> K;
  for (int i = 1; i <= N; i++) cin >> input[i];
  for (int i = 1; i <= N; i++) now[i] = (input[i] != 0 ? input[i] : -1);
  for (int i = 1; i <= N; i++) add(i, now[i]);
  // æŠŠæ‰€æœ‰æ¢ä»¶ä¾ç…§å³ç•Œçš„é †åº
  for (int i = 0; i < K; i++) {
    int l, r, c;
    cin >> l >> r >> c;
    hook[r].push_back({l, c});
  }
  for (int i = 1; i <= N; i++) {
    // å¦‚æœç¾åœ¨é€™æ ¼æ˜¯ç©ºçš„ï¼Œå°±æŠŠè¨»æ¨™å¡é€²å †ç–Šè£¡å‚™ç”¨ã€‚
    if (input[i] == 0) stack.push_back(i);

    // é€ä¸€æƒéæ‰€æœ‰å³ç•Œåœ¨ i çš„æ¢ä»¶ï¼Œä¸¦è©¦åœ–æ»¿è¶³ä»–å€‘ã€‚
    for (auto [l, c] : hook[i]) {
      int v = ask(l, i);
      // å¦‚æœç•¶å‰ç¸½å’Œä»ä¸é” cï¼Œå°±å¿…é ˆæŠŠæœ€æ¥è¿‘çš„ -1 æ”¹æˆ 1ã€‚
      while (v < c) {
        if (stack.empty() || stack.back() < l) {
          puts("Impossible");
          return 0;
        }
        v += 2;
        now[stack.back()] = 1;
        add(stack.back(), 2);
        stack.pop_back();
      }
    }
  }
  for (int i = 1; i <= N; i++) {
    cout << now[i] << ' ';
  }
  cout << endl;
  return 0;
}

#include <algorithm>
#include <iostream>
#include <queue>
using namespace std;

int a[505][505];
int deg[505];

int main() {
  int n, m;
  cin >> n >> m;
  for (int i = 0; i < m; i++) {
    int x, y;
    cin >> x >> y;
    a[x][y] = a[y][x] = 1;
    deg[x]++;
    deg[y]++;
  }
  int k;
  for (k = 2 * (n - 2); k >= 0; k--) {
    queue<pair<int, int>> q;
    // ç¬¬ä¸€æ­¥
    for (int x = 1; x <= n; x++)
      for (int y = x + 1; y <= n; y++)
        if (!a[x][y] && deg[x] + deg[y] >= k) {
          q.push({x, y});
        }
    // ç¬¬äºŒæ­¥
    while (!q.empty()) {
      auto [x, y] = q.front();
      q.pop();
      if (a[x][y]) continue;
      m++;
      a[x][y] = a[y][x] = 1;
      deg[x]++;
      deg[y]++;
      for (int z = 1; z <= n; z++) {
        if (x != z && !a[x][z] && deg[x] + deg[z] >= k) q.push({x, z});
        if (y != z && !a[y][z] && deg[y] + deg[z] >= k) q.push({y, z});
      }
    }
    // ç¬¬ä¸‰æ­¥
    if (m >= n * (n - 1) / 2) break;
  }
  cout << k << endl;
  return 0;
}

