// ç”¢ç”Ÿä¸€å€‹å­—ä¸²çš„æ‰€æœ‰å­åºåˆ—ã€‚
void GetAllSubstrings(string s, vector<string>& result) {
  result.push_back("");
  for (int i = 0; i < (int)s.size(); i++) {
    int n = result.size();
    for (int j = 0; j < n; j++) {
      result.push_back(result[j]+s[i]);
    }
  }
  // æŠŠæ‰¾åˆ°çš„å­åºåˆ—ä¾ç…§å­—å…¸é †åºæ’åºï¼Œæ³¨æ„é€™é‚Š result[0] æœƒæ˜¯ç©ºå­—ä¸²ã€‚
  sort(result.begin(), result.end());
}

int main() {
  int N;
  cin >> N;
  for (int i = 0; i < N; i++) cin >> S[i];
  for (int i = 0; i < N; i++) GetAllSubstrings(S[i], a[i]);

  const int INF = 1e5;
  // åˆå§‹åŒ–å‹•æ…‹è¦åŠƒçš„é¦–æ’ã€‚
  dp[0][0] = -INF; // ä¸å…è¨±æœ‰ç©ºå­—ä¸²ï¼Œæ‰€ä»¥çµ¦ä»– -INFã€‚
  for (int i = 1; i < (1<<S[0].size()); i++)
    dp[0][i] = max(dp[0][i-1], (int)a[0][i].size());

  // ç„¶å¾Œä¾ç…§éè¿´é—œä¿‚è¨ˆç®—å‰©ä¸‹çš„éƒ¨ä»½ã€‚
  for (int i = 1; i < N; i++) {
    dp[i][0] = -INF; // ä¸€æ¨£ä¸å…è¨±æœ‰ç©ºå­—ä¸²ï¼Œæ‰€ä»¥çµ¦ä»– -INFã€‚
    for (int j = 1, k = 0; j < (1<<S[i].size()); j++) {
        while (k < (1<<S[i-1].size()) && a[i-1][k] < a[i][j]) ++k;
        dp[i][j] = max(dp[i][j-1], dp[i-1][k-1] + (int)a[i][j].size());
    }
  }

  int ans = dp[N-1][(1<<S[N-1].size())-1];
  if (ans < 0) cout << "-1" << endl;
  else cout << ans << endl;
  return 0;
}

