int neighbor(int eid, int u) { return x[eid] + y[eid] - u; }

// å¦‚æœ dfs å›ä¾†ä»¥å¾Œé‚„æœ‰ä¸€æ¢é‚Šæ²’æœ‰è¢«é…å°ï¼Œé‚£å°±å›å‚³é€™æ¢é‚Šï¼Œå¦å‰‡å›å‚³ -1ã€‚
int dfs(int u, int from=-1) {
  int at_hand = -1;
  // ä¾åºè€ƒæ…®éæ‰€æœ‰çš„é‚Šï¼Œä½†æ˜¯å› ç‚º dfs æœƒç¶“éåŒä¸€å€‹é»å¾ˆå¤šæ¬¡ï¼Œ
  // æ‰€ä»¥ä¸å¦¨ç”¨ä¸€å€‹é¡ä¼¼ stack çš„æ–¹å¼å¯¦ä½œï¼Œæ¯èµ°éä¸€æ¢é‚Šå°±æŠŠé€™æ¢é‚Šå»æ‰ã€‚
  while (!a[u].empty()) {
    int eid = a[u].back();
    a[u].pop_back();
    if (used[eid]) continue;
    used[eid] = true;
    int v = neighbor(eid, u);
    int ret = dfs(v, eid);
    if (ret == -1) continue;
    if (at_hand == -1) {
      at_hand = ret;
    } else {
      ans.emplace_back(v, u, neighbor(at_hand, u));
      at_hand = -1;
    }
  }
  if (at_hand != -1 && from != -1) {
    ans.emplace_back(neighbor(at_hand, u), u, neighbor(from, u));
    return -1;
  } else if (from != -1) {
    return from;
  }
  return -1;
}

int main() {
  cin >> N >> M;
  for (int i = 0; i < M; i++) {
    cin >> x[i] >> y[i];
    a[x[i]].push_back(i);
    a[y[i]].push_back(i);
  }
  // å°æ–¼æ¯å€‹é»é€²è¡Œ DFSï¼Œå¦‚æœæ—©å°±æ¢ç´¢å®Œäº†ï¼Œé‚£ç›¸é„°é‚Šä¹Ÿéƒ½æœƒè¢«æ‹”å…‰ï¼Œæ‰€ä»¥ä¸ç”¨æ“”å¿ƒé‡è¤‡æœç´¢ã€‚
  for (int i = 1; i <= N; i++) dfs(i);

  cout << ans.size() << endl;
  // C++17 Structural Binding å¥½ç‰©ï¼Œè¶Šä¾†è¶Šåƒ python äº†ã€‚
  for (auto [u, v, w]: ans)
    cout << u << " " << v << " " << w << '\n';
  return 0;
}

#include <bits/stdc++.h>
using namespace std;

struct Edge {
  int to;
  int capacity;
  int reversed_index;
  char ch;
  Edge(int _to, int _c, int _r, char _ch): to(_to), capacity(_c), reversed_index(_r), ch(_ch) {}
};

class FlowNetwork {
  public:
    int nid, sink;
    vector<vector<Edge>> edges;
    FlowNetwork() : nid(0) {
      edges.clear();
    }
    void SetSink(int _sink) { sink = _sink; }
    int AddNode() {
      edges.push_back(vector<Edge>());
      return nid++;
    }
    // æ¯åŠ å…¥ä¸€æ¢é‚Šï¼ŒåŒæ™‚åŠ ä¸Šä¸€æ¢é€†å‘é‚Šï¼Œåˆ°æ™‚å€™å¢å»£çš„æ™‚å€™æˆ‘å€‘å¿…é ˆåœ¨å‰©é¤˜åœ–ä¸Šçš„åå‘é‚ŠåŒæ­¥æ“ä½œï¼Œ
    // å› æ­¤å¾ˆéœ€è¦èƒ½å¤ å¾ä¸€æ¢é‚Šè·³åˆ°å¦ä¸€æ¢ã€‚
    void AddEdge(int from, int to, char mark) {
      edges[from].emplace_back(to, 1, (int)edges[to].size(), mark);
      edges[to].emplace_back(from, 0, (int)edges[from].size()-1, '#');
    }

    // çµ¦å®šæºé» xï¼Œæ‰¾ä¸€æ¢ cost æœ€å°çš„è·¯å¾‘å¾ x åˆ° sinkã€‚å¦‚æœæ‰¾åˆ°äº†ï¼Œé †ä¾¿æŠŠé€™æ¢è·¯å¾‘å¢å»£ä¸€ä¸‹ã€‚
    bool SPFA(int x) {
      priority_queue<pair<int, int>> Q;
      vector<int> dist(nid, 1e9);
      vector<Edge> last(nid, Edge(-1,-1,-1,'X'));
      dist[x] = 0;
      Q.push({0, x});

      while (!Q.empty()) {
        auto it = Q.top(); Q.pop();
        int d = -it.first;
        int x = it.second;
        if (d != dist[x]) continue;

        for (auto&& e : edges[x]) {
          if (e.capacity) {
            int nd = d + (e.ch == '#' ? -1 : 1);
            if (dist[e.to] > nd) {
              dist[e.to] = nd;
              last[e.to] = edges[e.to][e.reversed_index];
              Q.push({ -nd, e.to });
            }
          }
        }
      }
      if (last[sink].to == -1) return false;
      x = sink;
      while (last[x].to != -1) {
        Edge& e = edges[last[x].to][last[x].reversed_index];
        e.capacity--;
        edges[e.to][e.reversed_index].capacity++;
        x = last[x].to;
      }
      return true;
    }

    // é€†å‘è¿½è¹¤é‚„åŸè·¯å¾‘ã€‚
    string Trace(int x) {
      string ret = "";
      while (x != sink) {
        for (auto&& e : edges[x]) {
          if (e.ch != '#' && e.capacity == 0) {
            x = e.to;
            if (e.ch != 'X') ret += e.ch;
          }
        }
      }
      return ret;
    }
};

