int ids[105][105][2];

bool solve() {
  int M, N;
  // è«‹ä¸è¦è®€åè¼¸å…¥ï¼Œé€™æ¨£æœƒ debug å¾ˆä¹…ï¼ˆæ·šï¼‰ã€‚
  cin >> N >> M;

  if (M == 0 && N == 0) return false;
  FlowNetwork g;

  // å…ˆå®šç¾©é€™å€‹åœ–ä¸Šçš„é»ã€‚
  for (int i = 1; i <= N; i++) {
    for (int j = 1; j <= M; j++) {
      ids[i][j][0] = g.AddNode();
      ids[i][j][1] = g.AddNode();
    }
  }

  int rB, cB, rC, cC, rG, cG, rU, cU;
  cin >> rB >> cB >> rC >> cC >> rG >> cG >> rU >> cU;

  // æŠŠåœ–ä¸Šçš„é‚Šå»ºç«‹èµ·ä¾†ï¼Œè¨˜å¾—è¦è·³é (rU, cU) é€™æ ¼ã€‚
  for (int x = 1; x <= N; x++) {
    for (int y = 1; y <= M; y++) {
      if (x == rU && y == cU) continue;
      g.AddEdge(ids[x][y][0], ids[x][y][1], 'X');
      const int dx[4] = {0, 1, 0, -1};
      const int dy[4] = {1, 0, -1, 0};
      const string dir = "RULD";
      for (int f = 0; f < 4; f++) {
        int nx = x + dx[f], ny = y + dy[f];
        if (nx == rU && ny == cU) continue;
        if (nx >= 1 && nx <= N && ny >= 1 && ny <= M) {
          g.AddEdge(ids[x][y][1], ids[nx][ny][0], dir[f]);
        }
      }
    }
  }

  g.SetSink(ids[rC][cC][0]);
  if (g.SPFA(ids[rB][cB][0]) && g.SPFA(ids[rG][cG][0])) {
    auto A = g.Trace(ids[rB][cB][0]);
    auto B = g.Trace(ids[rG][cG][0]);
    reverse(B.begin(), B.end());
    for (auto&& x : B) {
      if (x == 'U') x = 'D';
      else if (x == 'D') x = 'U';
      else if (x == 'L') x = 'R';
      else if (x == 'R') x = 'L';
    }
    cout << "YES" << '\n';
    cout << A << B << '\n';
  } else {
    cout << "NO" << '\n';
  }

  return true;
}

int main() {
  while (solve());
  return 0;
}

