typedef long long LL;

LL bigmod(LL a, LL n, LL mod) {
  if (n == 0) return 1%mod;
  if (n == 1) return a%mod;
  LL r = bigmod(a*a%mod, n/2, mod);
  if (n%2) r = r*a%mod;
  return r;
}

int main() {
  const LL mod = 1e9+7;
  LL ret = 1;
  int N, total = 0;
  cin >> N;
  for (int i = 0; i < N; i++) {
    LL ai, bi;
    cin >> ai >> bi;
    total ^= ((ai%2) ^ (bi%2));
    ret = ret * bigmod(2, ai*bi-(i==0), mod) % mod;
  }
  if (total) cout << 0 << endl;
  else cout << ret << endl;
  return 0;
}

