int lower_bound(int array[], auto predicate_fn) {
    int l, r, ans = -1;
    while(l <= r) {
        int m = (l + r) / 2;
        if (predicate_fn(array[m]) == true) {
            // å¦‚æœæ»¿è¶³æ¢ä»¶ï¼Œå°±æŠŠå³é‚Šä¸Ÿæ‰ï¼Œä¸¦ä¸”æŠŠç•¶å‰è³‡æ–™åŠ å…¥å¯èƒ½çš„ç­”æ¡ˆè£¡ã€‚
            ans = m;
            r = m - 1;
        } else {
            // å¦‚æœä¸æ»¿è¶³æ¢ä»¶ï¼Œå°±æŠŠå·¦é‚Šä¸Ÿæ‰ã€‚
            l = m + 1;
        }
    }
    return ans;
}

bool isprime(int n) {
    for (int i = 2; i * i <= n; i++) {
        if (n % i == 0)
            return false;
    }
    return true;
}

bool isPowerOfThree(int n) {
    for (long long i = 1; i <= n; i *= 3)
        if (n == i)
            return true;
    return false;
}

