// å°æ–¼æšèˆ‰çš„ s(x) å€¼ tï¼Œæ‰¾å‡ºåˆæ³•çš„è§£ï¼Œç”±æ–¼è§£æœƒæ˜¯ä¸€æ­£ä¸€è² ï¼Œæˆ‘å€‘åªå›å‚³æ­£çš„é‚£å€‹ã€‚
long long getsol(int t, long long n) {
	long long r = t*t+n*4, v = 0;
	v = sqrt(r);
	while(v*v<r) v++; while(v*v>r) v--;
	if(v*v!=r) return -1;
	v-=t; if(v%2 || v<0) return -1;
	v/=2;
	return v;
}

int main(void) {
	long long n, x, t, ans=-1;
	cin >> n;
	for (t = 1; t <= 81; t++) {
		x = getsol(t, n);
		if(x<0) continue;
		if(s(x)==t && ( ans==-1 || ans > x)) ans = x;
	}
	cout << ans << endl;
	return 0;
}

