int cnt[5][100000];

void pre_compute() {
  // å…ˆç”¨ç¯©æ³•æ±‚è³ªæ•¸ã€‚
  vector<int> sieved(99999, 0);
  for (int i = 2; i*i <= 99999; i++)
    for (int j = i*i; j <= 99999; j += i)
      sieved[j] = 1;
  // å°æ–¼æ‰¾åˆ°çš„æ¯ä¸€å€‹è³ªæ•¸ï¼Œæˆ‘å€‘è€ƒæ…®ä¸­é–“æ¯ä¸€å€‹ä½æ•¸è¢«æŒ–ç©ºä»¥å¾Œï¼Œå‰©ä¸‹çš„æ•¸å­—ï¼ŒæŠŠä»–åŠ é€²å»ã€‚
  for (int p = 2; p <= 99999; p++)
    if (sieved[p] == 0)
      for (int ten = 1, k = 0; ten <= 10000; ten *= 10, k++)
        cnt[k][ p/(ten*10)*ten + p%ten ]++;
}

int ans, n;
int a[6][6];
int get_count(int x) {
  int prefix = 0;
  for (int i = 0; i < n; i++)
    if (i != x)
      prefix = prefix*10 + a[x][i];
  return cnt[n-1-x][prefix];
}
void dfs(int x, int y, int total) {
  if (x == n) {
    ans += total;
    return;
  }
  if (y == n) {
    dfs(x+1, 0, total * get_count(x));
    return;
  }

  if (x >= y) {
    a[x][y] = a[y][x];
    dfs(x, y+1, total);
  } else {
    for (int i = 0; i <= 9; i++) {
      a[x][y] = i;
      dfs(x, y+1, total);
    }
  }
}

