/* by tmt514 */
#include <iostream>
#include <vector>
#include <queue>
using namespace std;

vector<string> forest = {
  "*****************",
  "*...*.......**..*",
  "**..*....*.*.*..*",
  "*......*.**.**.**",
  "*..**...**..**.**",
  "**.....**..*.*..*",
  "*....*..........*",
  "*.....****.*...**",
  "****.*.*........*",
  "*****************",
};

const int dx[4] = {0, 1, 0, -1};
const int dy[4] = {1, 0, -1, 0};
int onFire[10][17];
int onEscape[10][17];

void bfs(int mark[10][17], int hasFire[10][17], int sx, int sy, int T) {
  queue<int> q;
  // 如果一開始就著火了，就應該直接死掉。
  if (hasFire && hasFire[sx][sy] <= T) return;
  mark[sx][sy] = T;
  q.push(sx);
  q.push(sy);
  while (!q.empty()) {
    int x = q.front(); q.pop();
    int y = q.front(); q.pop();
    int t = mark[x][y];
    // 依序考慮四個方向是否可通行，如果可通行的話加到佇列裡面。
    for (int dir = 0; dir < 4; dir++) {
      int nx = x + dx[dir];
      int ny = y + dy[dir];
      if (forest[nx][ny] == '.' && 
          mark[nx][ny] == 0 &&
          (!hasFire || hasFire[nx][ny] == 0 || hasFire[nx][ny] > t+1)) {
        mark[nx][ny] = t+1;
        q.push(nx);
        q.push(ny);
      }
    }
  }
}

int main() {
  int fx, fy, sx, sy, ex, ey, T;
  cin >> fx >> fy >> T;
  cin >> sx >> sy >> ex >> ey;
  // 由於火勢不會燒到避難處，所以一開始要取巧把這格改掉。
  forest[ex][ey] = 'E';
  bfs(onFire, NULL, fx, fy, 1);
  // 然後把它改回變成可以通行。
  forest[ex][ey] = '.';
  bfs(onEscape, onFire, sx, sy, T);
  if (onEscape[ex][ey] > 0) {
    cout << onEscape[ex][ey]-T << endl;
  } else {
    cout << "Help!" << endl;
  }
  return 0;
}
