const int WINDOW = 500000000;
const int FULL = 1e9;
int MakeQuery(int x) {
  cout << "QUERY " << x << " " << (x + WINDOW-1)%FULL << endl;
  int ret;
  cin >> ret;
  return ret;
}

int GetMiddlePoint(int l, int r) {
  int mid = (l > r?
    (l + r + FULL) / 2 :
    (l + r) / 2
  );
  return mid % FULL;
}

