function*(input, ui) {
    var forest = [
        "*****************",
        "*...*.......**..*",
        "**..*....*.*.*..*",
        "*......*.**.**.**",
        "*..**...**..**.**",
        "**.....**..*.*..*",
        "*....*..........*",
        "*.....****.*...**",
        "****.*.*........*",
        "*****************",
    ];
    forest = forest.map((e) => e.split(""));
    var fx = input.fx;
    var fy = input.fy;
    var T = input.T;
    var sx = input.sx;
    var sy = input.sy;
    var ex = input.ex;
    var ey = input.ey;
    forest[fx][fy] = 'F';
    forest[ex][ey] = 'E';
    yield {forest: forest};
    var t, i, j, f;
    var d = [[0, 1], [1, 0], [0, -1], [-1, 0]];
    for (t = 0; t < T; t++) {
        var newforest = JSON.parse(JSON.stringify(forest));
        for (i = 0; i < forest.length; i++) {
            for (j = 0; j < forest[0].length; j++) {
                if (forest[i][j] === 'F') {
                    for (f = 0; f < 4; f++) {
                        if (forest[i+d[f][0]][j+d[f][1]] === '.') {
                           newforest[i+d[f][0]][j+d[f][1]] = 'F'; 
                        }
                    }
                }
            }
        }
        forest = newforest;
        yield {forest: forest};
    }
    return {forest: forest};
}
