#include <bits/stdc++.h>
using namespace std;

const int WINDOW = 500000000;
const int FULL = 1e9;
int MakeQuery(int x) {
  cout << "QUERY " << x << " " << (x + WINDOW-1)%FULL << endl;
  int ret;
  cin >> ret;
  return ret;
}

int GetMiddlePoint(int l, int r) {
  int mid = (l > r?
    (l + r + FULL) / 2 :
    (l + r) / 2
  );
  return mid % FULL;
}

int main() {
  int N;
  cin >> N;

  int vl = MakeQuery(0);
  int vr = N - vl;
  int l = 0, r = WINDOW;

  if (vl == vr) {
    cout << "YES " << 0 << endl;
    return 0;
  }

  if (vl > vr) swap(l, r);

  while (l != r) {
    int m = GetMiddlePoint(l, r);
    int v = MakeQuery(m);
    if (v == N/2) {
      cout << "YES " << m << endl;
      return 0;
    } else if (v > N/2) {
      r = (m+FULL-1)%FULL;
    } else {
      l = (m+1)%FULL;
    }
  }

  cout << "YES " << l << endl;
  return 0;
}
