#include <bits/stdc++.h>
using namespace std;

bool proper = true;
bool correct = true;

string S;

string RemoveSpace(string& S) {
  string T = "";
  for (auto x: S) if (x != ' ') T += x;
  return T;
}

// 合法的序列會是 x+x+x+...+x
void UpdateCorrectness(vector<char>& c) {
  if (c.size()%2==0) { correct = false; return; }
  for (size_t i = 1; i < c.size(); i++)
    if (c[i] == c[i-1]) {
      correct = false;
      return;
    }
  if (c[0] != 'x') { correct = false; return; }
}

int main() {
  getline(cin, S);
  S = RemoveSpace(S);
  vector<vector<char>> levels(1);
  for (size_t i = 0, now = 1; i < S.size(); i++) {
    if (S[i] == '(') {
      levels.back().push_back('x');
      ++now;
      levels.resize(now);
    } else if (S[i] == ')') {
      UpdateCorrectness(levels.back());
      if (levels.back().size() != 3) proper = false;
      levels.pop_back();
      --now;
      if (now <= 0) {
        correct = false;
        break;
      }
      levels.resize(now);
    } else if (S[i] >= 'a' && S[i] <= 'z') {
      levels.back().push_back('x');
    } else {
      levels.back().push_back('+');
    }
  }

  if (levels.size() != 1) {
    correct = false;
  } else {
    UpdateCorrectness(levels[0]);
    if (S[0] == '(' && levels[0].size() == 1) proper = false;
    if (levels[0].size() != 1 && levels[0].size() != 3) proper = false;
  }

  if (correct && proper) {
    cout << "proper" << endl;
  } else if (correct && !proper) {
    cout << "improper" << endl;
  } else if (!correct) {
    cout << "error" << endl;
  }
  return 0;
}
