#include <algorithm>
#include <cmath>
#include <cstdio>
#include <cstdlib>
#include <cstring>
#include <iostream>
#include <string>
#include <vector>
#define SZ(x) ((int)(x).size())
#define FOR(it, c) for(__typeof((c).begin()) it = (c).begin(); it != (c).end(); ++it)
using namespace std;
typedef long long LL;

void solve() {
  int K, N;
  scanf("%d%d", &K, &N);
  int ncorner = 0;
  int nsidex = 0;
  int nsidey = 0;
  int ninside = 0;
  for(int i=0;i<K;i++) {
    int x, y;
    scanf("%d%d", &x, &y);
    int sx = (x==0 || x==N-1);
    int sy = (y==0 || y==N-1);
    if(sx && sy) { ncorner++; }
    else if(sx) { nsidex++; }
    else if(sy) { nsidey++; }
    else ninside++;
  }
  if (ncorner==0 && nsidex==1 && nsidey==1 && ninside==0) puts("no");
  else
  puts(4*ncorner + 2*nsidex + 2*nsidey + ninside >= 4? "yes": "no");
}

int main(void) {
  int T;
  scanf("%d", &T);
  while(T--) solve();
  return 0;
}
