#include <bits/stdc++.h>
using namespace std;
typedef long long LL;

const LL MOD = 1e9 + 7;

LL bigmod(LL a, LL n) {
  LL b = 1;
  while (n) {
    if (n % 2)
      b = b * a % MOD;
    a = a * a % MOD;
    n /= 2;
  }
  return b;
}

LL npower[5005];
LL X[5005], Y[5005];

int main() {
  int N, R, C, K;
  cin >> N >> R >> C >> K;
  for (int i = 1; i <= 5000; i++)
    npower[i] = bigmod(i, N);
  for (int r = 0; r <= R; r++) {
    for (int i = 0; i <= R - r; i++) {
      X[r] += (npower[i + 1] - npower[i]) *
              (npower[R - r - i + 1] - npower[R - r - i]) % MOD;
    }
    X[r] = (X[r] % MOD + MOD) % MOD;
  }
  for (int c = 0; c <= C; c++) {
    for (int i = 0; i <= C - c; i++) {
      Y[c] += (npower[i + 1] - npower[i]) *
              (npower[C - c - i + 1] - npower[C - c - i]) % MOD;
    }
    Y[c] = (Y[c] % MOD + MOD) % MOD;
  }

  LL ans = 0;
  for (int r = 1; r <= R; r++)
    for (int c = 1; c <= C; c++) {
      if (r * c >= K) {
        ans += X[r] * Y[c] % MOD;
      }
    }
  cout << ans % MOD << endl;
  return 0;
}
