#include <bits/stdc++.h>
using namespace std;

bool solve() {
  int N, M;
  cin >> N >> M;
  if (N == -1 && M == -1) return false;

  if (M == N) { // Cycle
    cout << "YES" << '\n';
    for (int i=1;i<=N;i++)
      cout << i << " " << (i==N?1: i+1) << '\n';
  } else if (M == N*(N-1)/2) { // Complete Graph
    cout << "YES" << '\n';
    for (int i=1;i<=N;i++)
      for(int j=i+1;j<=N;j++)
        cout << i << " " << j << '\n';
  } else if (N%2==0 && M==(N/2)*(N/2)) { // Complete Bipartite Graph
    cout << "YES" << '\n';
    for (int i=1;i<=N/2;i++)
      for(int j=N/2+1;j<=N;j++)
        cout << i << " " << j << '\n';
  } else {
    cout << "NO" << '\n';
  }

  return true;
}

int main() {
  while(solve());
  return 0;
}
