#include <bits/stdc++.h>
using namespace std;

struct Edge {
  int to, cap, rev;
  Edge(int _to, int _cap, int _rev): to(_to), cap(_cap), rev(_rev) {}
};

class FlowNetwork {
  public:
    int n, sink;
    vector<vector<Edge>> adj;
    int mark;
    vector<int> marks;
    FlowNetwork(int _n, int _sink): n(_n), sink(_sink) {
      adj.resize(n);
      marks.resize(n, 0);
      mark = 1;
    }
    void AddEdge(int x, int y, int c = 1) {
      adj[x].emplace_back(y, c, adj[y].size());
      adj[y].emplace_back(x, 0, adj[x].size()-1);
    }
    bool _DFS(int x) {
      marks[x] = mark;
      if (x == sink) return true;
      for (auto& e: adj[x]) {
        if (marks[e.to] != mark && e.cap > 0 && _DFS(e.to)) {
          adj[e.to][e.rev].cap++;
          e.cap--;
          return true;
        }
      }
      return false;
    }
    bool DFS(int s) {
      if (_DFS(s)) {
        mark++;
        return true;
      }
      return false;
    }
};

int main() {
  int N, K, M;
  cin >> N >> K >> M;
  FlowNetwork g(N*K+1, N*K);
  auto enc = [&](int i, int j) {
    return (i-1)*K+j;
  };
  for (int j = 0; j < K; j++)
    g.AddEdge(enc(N, j), g.sink, K);
  while (M--) {
    int x, y;
    cin >> x >> y;
    for (int j = 0; j < K; j++)
      g.AddEdge(enc(x, j), enc(y, (j+1)%K));
  }
  int ans = 0;
  for (int i = 1; i <= K; i++) {
    if (g.DFS(enc(i, i%K)))
      ++ans;
  }
  cout << ans << endl;
  return 0;
}
