#include <bits/stdc++.h>
using namespace std;

int main() {
  int m, n, w, h;
  cin >> m >> n >> w >> h;
  vector<int> ws(m), d(n + 1);
  for (int i = 0; i < m; i++) {
    cin >> ws[i];
    ws[i] /= w;
  }
  for (int i = 1; i <= n; i++)
    cin >> d[i];
  vector<int> jobs;
  vector<vector<int>> schedule(m);
  int bad = 0;
  for (int i = 1; !bad && i <= n; i++) {
    if (d[i] > 0 && i + w - 1 > n) {
      bad = 1;
      break;
    }
    while (d[i] > 0) {
      jobs.push_back(i);
      for (int j = 0; j < w; j++) {
        if (--d[i + j] < 0)
          bad = 1;
      }
    }
  }

  priority_queue<pair<int, int>> s;
  for (int i = 0; i < m; i++)
    s.push({ws[i] - schedule.size(), i});
  vector<vector<pair<int, int>>> delay(n + 1);
  int now = 0;

  for (int t : jobs) {
    while (now <= t) {
      for (auto x : delay[now])
        s.push(x);
      now++;
    }
    if (s.empty()) {
      bad = 1;
      break;
    }
    auto [remain, i] = s.top();
    s.pop();
    schedule[i].push_back(t);
    if (t + w + h <= n)
      delay[t + w + h].push_back({remain - 1, i});
  }
  for (int i = 0; i < m; i++) {
    if (schedule[i].size() != ws[i])
      bad = 1;
  }
  if (bad) {
    cout << "-1" << endl;
  } else {
    cout << "1";
    for (int i = 0; i < m; i++) {
      for (int j = 0; j < schedule[i].size(); j++) {
        cout << (j == 0 ? '\n' : ' ');
        cout << schedule[i][j];
      }
    }
    cout << '\n';
  }
  return 0;
}
