#include <bits/stdc++.h>
using namespace std;

const int MAXB = 400005;
int r[MAXB], c[MAXB];

const vector<string> DSTR = {">", "<", "v", "^"};
string s[MAXB];
int face[MAXB];
int visit[MAXB];
int link[MAXB][4];
vector<pair<int, int>> rows[MAXB], cols[MAXB];

// 定義四個方向。
enum {
  LEFT = 0,
  RIGHT = 1,
  UP = 2,
  DOWN = 3
};

inline bool IsNeighbor(int x, int y) {
  return abs(r[x]-r[y]) + abs(c[x]-c[y]) == 1;
}

void dfs(int x) {
  visit[x] = 1;
  for (int dir = 0; dir < 4; dir++) {
    int y = link[x][dir];
    if (IsNeighbor(x, y) && !visit[y]) {
      face[y] = (dir^1);
      s[y] = DSTR[dir] + " " + to_string(dir/2? c[y]: r[y]);
      dfs(y);
    }
  }
}

// 紀錄優先順序 (得先有 y 才能有 x)。
vector<int> deps[MAXB];
inline void AddLink(int x, int y) {
  if (x == -1 || y == -1) return;
  deps[x].push_back(y);
}

// 紀錄解答用的。
vector<int> solution;
bool no_solution = false;
void topological_sort(int x) {
  visit[x] = 1;
  for (auto y : deps[x]) {
    if (visit[y] == 0)
      topological_sort(y);
    else if (visit[y] == 1)
      no_solution = 1;
  }
  solution.push_back(x);
  visit[x] = 2;
}

int main() {
  int N, M, B;
  cin >> N >> M >> B;
  for (int i = 0; i < B; i++) {
    cin >> r[i] >> c[i];
    rows[r[i]].push_back({c[i], i});
    cols[c[i]].push_back({r[i], i});
  }
  for (int i = 1; i < MAXB; i++) {
    sort(rows[i].begin(), rows[i].end());
    sort(cols[i].begin(), cols[i].end());
  }

  memset(link, -1, sizeof(link));
  for (int i = 1; i < MAXB; i++) {
    for (size_t j = 1; j < rows[i].size(); j++) {
      int prev = rows[i][j-1].second;
      int now = rows[i][j].second;
      link[prev][RIGHT] = now;
      link[now][LEFT] = prev;
    }
    for (size_t j = 1; j < cols[i].size(); j++) {
      int prev = cols[i][j-1].second;
      int now = cols[i][j].second;
      link[prev][DOWN] = now;
      link[now][UP] = prev;
    }
  }
  dfs(0);
  for (int x = 1; x < B; x++) {
    AddLink(x, link[x][face[x]]);
    AddLink(link[x][face[x]^1], x);
  }
  memset(visit, 0, sizeof(visit));
  for (int x = 0; x < B; x++) {
    if (!visit[x])
      topological_sort(x);
  }
  if (no_solution) {
    cout << "impossible" << endl;
  } else {
    cout << "possible" << '\n';
    for (auto x : solution)
      if (x != 0)
        cout << s[x] << '\n';
  }
  return 0;
}
